var counter=0,$items=$(".hero-slideshow figure"),numItems=$items.length,showCurrent=function(){var e=Math.abs(counter%numItems);$items.removeClass("show"),$items.eq(e).addClass("show")};$(".next").on("click",function(){counter++,showCurrent()}),$(".prev").on("click",function(){counter--,showCurrent()}),$(".open-modal").click(function(){for(var e=$(this)[0],t=e.className.split(/\s+/),o=0;o<t.length;o++)if(t[o].match(/modal-/)){var a=t[o];$(".modal."+a).fadeIn("fast")}}),$(".close-modal").click(function(){$(this).closest(".modal").fadeOut("fast")}),$("button").click(function(e){$(".overlay, .modal").addClass("active"),e.preventDefault()}),$(".modal img").click(function(e){$(".overlay, .modal").removeClass("active"),e.preventDefault()});