var counter=0,$items=$(".hero-slideshow figure"),numItems=$items.length,showCurrent=function(){var t=Math.abs(counter%numItems);$items.removeClass("show"),$items.eq(t).addClass("show")};$(".next").on("click",function(){counter++,showCurrent()}),$(".prev").on("click",function(){counter--,showCurrent()}),$(".open-modal").click(function(){for(var t=$(this)[0],e=t.className.split(/\s+/),s=0;s<e.length;s++)if(e[s].match(/modal-/)){var o=e[s];$(".modal."+o).fadeIn("fast")}}),$(".close-modal").click(function(){$(this).closest(".modal").fadeOut("fast")});